mapper.select:
SELECT FROM pst_dag_job AS t
AND t.status = #{status}
AND t.job_desc like CONCAT(CONCAT('%', #{jobDesc}), '%')
AND t.job_name like CONCAT(CONCAT('%', #{jobName}), '%')
ORDER BY id DESC LIMIT #{offset}, #{pagesize}

SELECT 
FROM pst_dag_job AS t
WHERE t.id = #{id}

mapper.update:
UPDATE pst_dag_job
SET
	dag_info = #{dagInfo}
WHERE id = #{id}

mapper.select:
SELECT 
FROM pst_dag_job AS t
WHERE t.status = 1
and t.trigger_next_time  <=  #{maxNextTime}
ORDER BY id ASC
LIMIT #{pagesize}

mapper.update:
UPDATE pst_dag_job
SET
	trigger_last_time = #{triggerLastTime},
	trigger_next_time = #{triggerNextTime},
	status = #{status}
WHERE id = #{id}
